mapper.select:
SELECT amount AS "amount", ccy AS "currency"
FROM core.transaction
WHERE tran_id = #{transactionId}

SELECT direction
FROM core.transaction
WHERE tran_id = #{transactionId}

SELECT status
FROM core.transaction
WHERE tran_id = #{transactionId}

SELECT description
FROM core.transaction
WHERE tran_id = #{transactionId}

SELECT acco_id
FROM core.transaction
WHERE tran_id = #{transactionId}

mapper.update:
UPDATE core.transaction
SET status = #{status}
WHERE tran_id = #{transactionId}
